import React, { useState } from 'react';
import './CreateIme.css';

function CreateIme() {
  const [formData, setFormData] = useState({
    caseNumber: '',
    patientName: '',
    patientDOB: '',
    insuranceCompany: '',
    attorneyName: '',
    attorneyEmail: '',
    attorneyPhone: '',
    injuryType: '',
    injuryDate: '',
    additionalNotes: '',
    files: [],
    privacyConsent: false
  });
  
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitMessage, setSubmitMessage] = useState('');
  const [fileNames, setFileNames] = useState([]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleFileChange = (e) => {
    const selectedFiles = Array.from(e.target.files);
    setFormData({
      ...formData,
      files: [...formData.files, ...selectedFiles]
    });
    
    const newFileNames = selectedFiles.map(file => file.name);
    setFileNames([...fileNames, ...newFileNames]);
  };

  const removeFile = (indexToRemove) => {
    setFormData({
      ...formData,
      files: formData.files.filter((_, index) => index !== indexToRemove)
    });
    
    setFileNames(fileNames.filter((_, index) => index !== indexToRemove));
  };

  const handleCheckboxChange = (e) => {
    const { name, checked } = e.target;
    setFormData({
      ...formData,
      [name]: checked
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setSubmitMessage('');
    
    try {
      // For now, just simulate the submission with a timeout
      await new Promise(resolve => setTimeout(resolve, 2000));
      
      console.log('Form data submitted:', formData);
      setSubmitMessage('IME successfully submitted! A doctor will review your case shortly.');
      
      // Reset form after successful submission
      setFormData({
        caseNumber: '',
        patientName: '',
        patientDOB: '',
        insuranceCompany: '',
        attorneyName: '',
        attorneyEmail: '',
        attorneyPhone: '',
        injuryType: '',
        injuryDate: '',
        additionalNotes: '',
        files: [],
        privacyConsent: false
      });
      setFileNames([]);
      
    } catch (error) {
      console.error('Error submitting form:', error);
      setSubmitMessage('Error submitting IME. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
  };

  const injuryTypes = [
    "Motor Vehicle Accident",
    "Slip and Fall",
    "Work-related Injury",
    "Medical Malpractice",
    "Product Liability",
    "Sports Injury",
    "Assault",
    "Other"
  ];

  return (
    <div className="create-ime-page">
      <h1>Submit Independent Medical Examination (IME)</h1>
      
      <div className="ime-form-container">
        {submitMessage && (
          <div className={`submission-message ${submitMessage.includes('Error') ? 'error' : 'success'}`}>
            {submitMessage}
          </div>
        )}
        
        <form className="ime-form" onSubmit={handleSubmit}>
          <div className="form-section">
            <h2>Case Information</h2>
            
            <div className="form-row">
              <div className="form-group">
                <label htmlFor="caseNumber">Case Number</label>
                <input 
                  type="text" 
                  id="caseNumber" 
                  name="caseNumber" 
                  value={formData.caseNumber}
                  onChange={handleInputChange}
                  placeholder="Enter case number" 
                  required 
                />
              </div>
              
              <div className="form-group">
                <label htmlFor="insuranceCompany">Insurance Company</label>
                <input 
                  type="text" 
                  id="insuranceCompany" 
                  name="insuranceCompany" 
                  value={formData.insuranceCompany}
                  onChange={handleInputChange}
                  placeholder="Insurance company name" 
                />
              </div>
            </div>
          </div>
          
          <div className="form-section">
            <h2>Patient Information</h2>
            
            <div className="form-row">
              <div className="form-group">
                <label htmlFor="patientName">Patient Name</label>
                <input 
                  type="text" 
                  id="patientName" 
                  name="patientName" 
                  value={formData.patientName}
                  onChange={handleInputChange}
                  placeholder="Full name of patient" 
                  required 
                />
              </div>
              
              <div className="form-group">
                <label htmlFor="patientDOB">Date of Birth</label>
                <input 
                  type="date" 
                  id="patientDOB" 
                  name="patientDOB" 
                  value={formData.patientDOB}
                  onChange={handleInputChange}
                  required 
                />
              </div>
            </div>
            
            <div className="form-row">
              <div className="form-group">
                <label htmlFor="injuryType">Injury Type</label>
                <select 
                  id="injuryType" 
                  name="injuryType" 
                  value={formData.injuryType}
                  onChange={handleInputChange}
                  required
                >
                  <option value="">Select injury type</option>
                  {injuryTypes.map((type, index) => (
                    <option key={index} value={type}>{type}</option>
                  ))}
                </select>
              </div>
              
              <div className="form-group">
                <label htmlFor="injuryDate">Date of Injury</label>
                <input 
                  type="date" 
                  id="injuryDate" 
                  name="injuryDate" 
                  value={formData.injuryDate}
                  onChange={handleInputChange}
                  required 
                />
              </div>
            </div>
          </div>
          
          <div className="form-section">
            <h2>Attorney Information</h2>
            
            <div className="form-row">
              <div className="form-group">
                <label htmlFor="attorneyName">Attorney Name</label>
                <input 
                  type="text" 
                  id="attorneyName" 
                  name="attorneyName" 
                  value={formData.attorneyName}
                  onChange={handleInputChange}
                  placeholder="Your full name" 
                  required 
                />
              </div>
            </div>
            
            <div className="form-row">
              <div className="form-group">
                <label htmlFor="attorneyEmail">Email</label>
                <input 
                  type="email" 
                  id="attorneyEmail" 
                  name="attorneyEmail" 
                  value={formData.attorneyEmail}
                  onChange={handleInputChange}
                  placeholder="Your email address" 
                  required 
                />
              </div>
              
              <div className="form-group">
                <label htmlFor="attorneyPhone">Phone</label>
                <input 
                  type="tel" 
                  id="attorneyPhone" 
                  name="attorneyPhone" 
                  value={formData.attorneyPhone}
                  onChange={handleInputChange}
                  placeholder="Your phone number" 
                  required 
                />
              </div>
            </div>
          </div>
          
          <div className="form-section">
            <h2>Medical Records</h2>
            
            <div className="form-group file-upload-container">
              <label htmlFor="filesUpload">Upload Medical Records (PDF)</label>
              <div className="file-upload-wrapper">
                <input 
                  type="file" 
                  id="filesUpload" 
                  name="filesUpload"
                  accept=".pdf"
                  multiple
                  onChange={handleFileChange}
                  className="file-input"
                />
                <div className="file-upload-button">
                  <span>Select Files</span>
                </div>
              </div>
              <p className="upload-help-text">Maximum file size: 100MB per file. You can upload multiple files.</p>
            </div>
            
            {fileNames.length > 0 && (
              <div className="uploaded-files">
                <h4>Uploaded Files:</h4>
                <ul>
                  {fileNames.map((name, index) => (
                    <li key={index}>
                      <span className="file-name">{name}</span>
                      <button 
                        type="button" 
                        className="remove-file-btn"
                        onClick={() => removeFile(index)}
                      >
                        Remove
                      </button>
                    </li>
                  ))}
                </ul>
              </div>
            )}
            
            <div className="form-group">
              <label htmlFor="additionalNotes">Additional Notes</label>
              <textarea 
                id="additionalNotes" 
                name="additionalNotes" 
                value={formData.additionalNotes}
                onChange={handleInputChange}
                rows="5" 
                placeholder="Any additional information relevant to the case..."
              ></textarea>
            </div>
          </div>
          
          <div className="form-section">
            <h2>Privacy Notice</h2>
            
            <div className="form-group privacy-consent">
              <input 
                type="checkbox" 
                id="privacyConsent" 
                name="privacyConsent"
                checked={formData.privacyConsent}
                onChange={handleCheckboxChange}
                required
              />
              <label htmlFor="privacyConsent">
                I have read and agree to the 
                <a href="/privacy-policy" target="_blank" rel="noopener noreferrer"> privacy policy</a>.
              </label>
            </div>
          </div>
          
          <div className="form-actions">
            <button 
              type="submit" 
              className="submit-button"
              disabled={isSubmitting || formData.files.length === 0}
            >
              {isSubmitting ? 'Submitting...' : 'Submit IME'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default CreateIme;
